require 'spec_helper'

# This spec was generated by rspec-rails when you ran the scaffold generator.
# It demonstrates how one might use RSpec to specify the controller code that
# was generated by the Rails when you ran the scaffold generator.

describe QuarterlyResultsController do

  def mock_quarterly_result(stubs={})
    @mock_quarterly_result ||= mock_model(QuarterlyResult, stubs).as_null_object
  end

  describe "GET index" do
    it "assigns all quarterly_results as @quarterly_results" do
      QuarterlyResult.stub(:all) { [mock_quarterly_result] }
      get :index
      assigns(:quarterly_results).should eq([mock_quarterly_result])
    end
  end

  describe "GET show" do
    it "assigns the requested quarterly_result as @quarterly_result" do
      QuarterlyResult.stub(:find).with("37") { mock_quarterly_result }
      get :show, :id => "37"
      assigns(:quarterly_result).should be(mock_quarterly_result)
    end
  end

  describe "GET new" do
    it "assigns a new quarterly_result as @quarterly_result" do
      QuarterlyResult.stub(:new) { mock_quarterly_result }
      get :new
      assigns(:quarterly_result).should be(mock_quarterly_result)
    end
  end

  describe "GET edit" do
    it "assigns the requested quarterly_result as @quarterly_result" do
      QuarterlyResult.stub(:find).with("37") { mock_quarterly_result }
      get :edit, :id => "37"
      assigns(:quarterly_result).should be(mock_quarterly_result)
    end
  end

  describe "POST create" do
    describe "with valid params" do
      it "assigns a newly created quarterly_result as @quarterly_result" do
        QuarterlyResult.stub(:new).with({'these' => 'params'}) { mock_quarterly_result(:save => true) }
        post :create, :quarterly_result => {'these' => 'params'}
        assigns(:quarterly_result).should be(mock_quarterly_result)
      end

      it "redirects to the created quarterly_result" do
        QuarterlyResult.stub(:new) { mock_quarterly_result(:save => true) }
        post :create, :quarterly_result => {}
        response.should redirect_to(quarterly_result_url(mock_quarterly_result))
      end
    end

    describe "with invalid params" do
      it "assigns a newly created but unsaved quarterly_result as @quarterly_result" do
        QuarterlyResult.stub(:new).with({'these' => 'params'}) { mock_quarterly_result(:save => false) }
        post :create, :quarterly_result => {'these' => 'params'}
        assigns(:quarterly_result).should be(mock_quarterly_result)
      end

      it "re-renders the 'new' template" do
        QuarterlyResult.stub(:new) { mock_quarterly_result(:save => false) }
        post :create, :quarterly_result => {}
        response.should render_template("new")
      end
    end
  end

  describe "PUT update" do
    describe "with valid params" do
      it "updates the requested quarterly_result" do
        QuarterlyResult.stub(:find).with("37") { mock_quarterly_result }
        mock_quarterly_result.should_receive(:update_attributes).with({'these' => 'params'})
        put :update, :id => "37", :quarterly_result => {'these' => 'params'}
      end

      it "assigns the requested quarterly_result as @quarterly_result" do
        QuarterlyResult.stub(:find) { mock_quarterly_result(:update_attributes => true) }
        put :update, :id => "1"
        assigns(:quarterly_result).should be(mock_quarterly_result)
      end

      it "redirects to the quarterly_result" do
        QuarterlyResult.stub(:find) { mock_quarterly_result(:update_attributes => true) }
        put :update, :id => "1"
        response.should redirect_to(quarterly_result_url(mock_quarterly_result))
      end
    end

    describe "with invalid params" do
      it "assigns the quarterly_result as @quarterly_result" do
        QuarterlyResult.stub(:find) { mock_quarterly_result(:update_attributes => false) }
        put :update, :id => "1"
        assigns(:quarterly_result).should be(mock_quarterly_result)
      end

      it "re-renders the 'edit' template" do
        QuarterlyResult.stub(:find) { mock_quarterly_result(:update_attributes => false) }
        put :update, :id => "1"
        response.should render_template("edit")
      end
    end
  end

  describe "DELETE destroy" do
    it "destroys the requested quarterly_result" do
      QuarterlyResult.stub(:find).with("37") { mock_quarterly_result }
      mock_quarterly_result.should_receive(:destroy)
      delete :destroy, :id => "37"
    end

    it "redirects to the quarterly_results list" do
      QuarterlyResult.stub(:find) { mock_quarterly_result }
      delete :destroy, :id => "1"
      response.should redirect_to(quarterly_results_url)
    end
  end

end
